'use strict'

const BB = require('cacache/lib/content/node_modules/bluebird')

const contentPath = require('cacache/lib/content/path')
const hasContent = require('cacache/lib/content/read').hasContent
const rimraf = BB.promisify(require('cacache/lib/content/node_modules/rimraf'))

module.exports = rm
function rm (cache, integrity) {
  return hasContent(cache, integrity).then(content => {
    if (content) {
      const sri = content.sri
      if (sri) {
        return rimraf(contentPath(cache, sri)).then(() => true)
      }
    } else {
      return false
    }
  })
}
